"use strict";var body=document.querySelector("body"),categories=document.querySelectorAll(".blog-filter"),all=document.querySelector(".blog-filter.all");if(body.classList.contains("blog")){var urlParams=new URLSearchParams(window.location.search),cat=urlParams.get("cat");cat?categories.forEach(function(a){a.dataset.id===cat&&a.classList.add("active")}):all.classList.add("active")}